<template>
  <div>
    <el-card class="box-card">
      <div slot="header" class="clearfix">
        <el-radio-group @change="radioChange" v-model="radio">
          <el-radio-button label="全部"></el-radio-button>
          <el-radio-button label="收藏"></el-radio-button>
        </el-radio-group>
      </div>
      <el-row>
        <el-col
          style="margin-bottom: 15px;padding:0 10px 0 10px;cursor: pointer;"
          :span="8"
          v-for="item in imgs"
          :key="item.id"
        >
          <el-card
            @click.native="activityCard(item)"
            :class="['card_item',activity.indexOf(item.id) != -1 ?'check':'']"
            :body-style="{ padding: '0px' }"
          >
            <div style="height:160px; display:flex; justify-content:center;align-items:center;">
              <img :src="item.url" style="height:100%" />
            </div>
          </el-card>
        </el-col>
      </el-row>

      <el-pagination
        @current-change="changePage"
        :page-size="12"
        background
        layout="prev, pager, next"
        :total="total"
      ></el-pagination>
    </el-card>
  </div>
</template>

<script>
import { getMedia } from "@/api/api";
export default {
  props:['imgs_'],
  data() {
    return {
      radio: "全部",
      imgs: [],
      total: 0,
      activity: [],
      urls:[]
    };
  },
  mounted() {},
  methods: {
    async getMedia(page = 1) {
      const res = await getMedia({
        per_page: 12,
        page: page,
        collect: this.radio == "收藏"
      });
      if (res.message == "OK") {
        this.total = res.data.total_count;
        this.imgs = res.data.results;
      }
    },
    radioChange() {
      this.getMedia();
    },
    changePage(page) {
      this.getMedia(page);
    },
    activityCard(item) {
      if (this.imgs_.length + this.urls.length >= 3 && this.activity.indexOf(item.id) == -1) return;
      this.activity.indexOf(item.id) != -1 ? this.activity.splice(this.activity.indexOf(item.id),1) : this.activity.push(item.id)
      this.urls.indexOf(item.url) != -1 ? this.urls.splice(this.urls.indexOf(item.url),1) : this.urls.push(item.url)
      this.$emit('checked',this.urls)
    },
  }
};
</script>

<style lang="less" scoped >
.card_item {
  position: relative;
}
.card_item.check::before {
  position: absolute;
  content: "";
  height: 100%;
  width: 100%;
  background-color: rgba(0, 0, 0, 0.3);
  background-image: url("data:image/png;base64,");
  background-repeat: no-repeat;
  background-position: right top;
  background-size: 40px;
}
</style>